import React, {useState} from 'react';
import Expenses from "./components/Expenses/Expenses";
import NewExpense from './components/NewExpense/NewExpense';


function App() {

  const [expenses, setExpenses] = useState([
    {
        id: 'e1',
        title: 'Toilet Paper',
        amount: 94.12,
        date: new Date(2020, 7, 14),
    },
    {
        id: 'e2',
        title: 'New TV',
        amount: 799.49,
        date: new Date(2021, 2, 12)
    },
    {
        id: 'e3',
        title: 'Car Insurance',
        amount: 294.67,
        date: new Date(2019, 2, 28),
    },
    {
        id: 'e4',
        title: 'New Desk (Wooden)',
        amount: 450,
        date: new Date(2021, 5, 12),
    },
  ]);

  const [filterValue, setFilterValue] = useState ('2022');

const saveNewExpense = (enteredData) => {
  const newElement = {...enteredData}
  setExpenses((prevState) => [...prevState, newElement])
  };

 const saveFilterValue = (value) => {
  setFilterValue(value);
  }

  return (
    <div >
      <NewExpense onNewExpense = {saveNewExpense}/>
      <Expenses expenses={expenses} changeFilterValue={saveFilterValue} selected={filterValue}/>
    </div>
  );
}

export default App;
