import React, { useState } from 'react';
import Card from '../UI/Card';
import ExpenseDate from './ExpenseDate';
import './ExpenseItem.css';
import ExpensesFilter from './ExpensesFilter';

function ExpenseItem(props) {

    const [title, setTitle] = useState(props.title)
    const [filterValue, setFilterValue] = useState(props.selected)

    const changeTitle = () => {
        setTitle('updated');
        console.log('updated')
    }

    const changeFilterValue = (value) => {
        setFilterValue(value);
        props.changeFilterValue(value);
    }

    const filteredExpenses =  props.expenses.filter(element => {
        return element.date.getFullYear().toString() === filterValue}
        );

        let expensesContent = <p>No expenses found.</p>;

        if (filteredExpenses.length > 0 ) {
            expensesContent = filteredExpenses.map(i => {
                return (
                    <div className='expense-item'>
                        <ExpenseDate key={i.id} date={i.date} />
                        <div className='expense-item__description'>
                            <h2>{i.title}</h2>
                            <div className='expense-item__price'>${i.amount}</div>
                            <button onClick={changeTitle}>change title</button>
                        </div>
                    </div>
            )})
        }

    return (
        <>
            <Card className='expense-item'>
                <ExpensesFilter onSelectedValue={changeFilterValue} selected={props.selected}/>
                {expensesContent}
            </Card>
        </>
    )
}

export default ExpenseItem;
